import React, { useState } from 'react';
import EditarUser from './EditarUser'; // Importe o componente EditarUser

const Tabela = ({ userData }) => {
  const [editUserId, setEditUserId] = useState(null); // Estado para armazenar o ID do usuário selecionado para edição

  // Função para lidar com o clique no botão de editar
  const handleEditClick = (userId) => {
    setEditUserId(userId); // Define o ID do usuário para edição
  };

  return (
    <div>
      <h2>Lista de Usuários</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Idade</th>
            <th>Rua</th>
            <th>Bairro</th>
            <th>Estado</th>
            <th>Biografia</th>
            <th>Imagem</th>
            <th scope="col" className="text-center">Ações</th>
          </tr>
        </thead>
        <tbody>
          {userData.map(usuario => (
            <tr key={usuario.id}>
              <td>{usuario.id}</td>
              <td>{usuario.nome}</td>
              <td>{usuario.idade}</td>
              <td>{usuario.rua}</td>
              <td>{usuario.bairro}</td>
              <td>{usuario.estado}</td>
              <td>{usuario.biografia}</td>
              <td><img src={"../img/" + usuario.imagem} alt={`Imagem de ${usuario.nome}`} width="100px" /></td>
              <td className="text-center">
                <button className="btn btn-warning" onClick={() => handleEditClick(usuario.id)}>Editar</button> {/* Passa o ID do usuário para a função handleEditClick */}
                <button className="btn btn-danger">Excluir</button>
                <button className="btn btn-success">Detalhar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      
    </div>
  );
};

export default Tabela;
