import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom'; // Importando componentes do React Router
import DetalhesUsuario from './DetalhesUsuario';
import EditarUser from './EditarUser';
import 'bootstrap/dist/css/bootstrap.min.css';

const Tabela = () => {
  const [userData, setUserData] = useState([]);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get('http://localhost:8081/listar');
        setUserData(response.data);
      } catch (error) {
        console.error('Erro ao buscar usuários:', error);
      }
    };

    fetchUserData();
  }, []);

  const handleDeleteClick = async (userId) => {
    try {
      await axios.delete(`http://localhost:8081/remover/${userId}`);
      const updatedUsers = userData.filter(user => user.id !== userId);
      setUserData(updatedUsers);
    } catch (error) {
      console.error('Erro ao excluir usuário:', error);
    }
  };

  return (
    <div>
      <h2>Lista de Usuários</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Idade</th>
            <th>Rua</th>
            <th>Bairro</th>
            <th>Estado</th>
            <th>Biografia</th>
            <th>Imagem</th>
            <th scope="col" className="text-center">Ações</th>
          </tr>
        </thead>
        <tbody>
          {userData.map(usuario => (
            <tr key={usuario.id}>
              <td>{usuario.id}</td>
              <td>{usuario.nome}</td>
              <td>{usuario.idade}</td>
              <td>{usuario.rua}</td>
              <td>{usuario.bairro}</td>
              <td>{usuario.estado}</td>
              <td>{usuario.biografia}</td>
              <td><img src={"../img/" + usuario.imagem} alt={`Imagem de ${usuario.nome}`} width="100px" /></td>
              <td className="text-center">
                <Link to={`/editar/${usuario.id}`} className="btn btn-warning">Editar</Link>
                <button className="btn btn-danger" onClick={() => handleDeleteClick(usuario.id)}>Excluir</button>
                <Link to={`/detalhes/${usuario.id}`} className="btn btn-success">Detalhar</Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Tabela;
